import React from 'react'

export class RaceList extends React.Component {
	state = {
		raceArr: [],
		selectedRace: ''
	}

	componentDidMount() {
		const classUrl = 'http://www.dnd5eapi.co/api/races/'

		fetch(classUrl)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(races => {
				return {
					value: races.name,
					name: races.name
				}
			})
			this.setState({
				raceArr: [{
					value: '',
					display: 'Choose Your Race!',
				}]
				.concat(temp)
			})
		}).catch(error => console.error('error'))
	}


	render(){
		return(
			<div>
			<select 
				value={this.state.selectedRace}
				onChange={event => this.setState({
					selectedRace: event.target.value
				})}>
				{
					this.state.raceArr.map((race) => 
						<option 
						key={race.value} 
						value={race.value}>
						{race.display}
						</option>)
				}
			</select>
			</div>
			)
	}

}
