import React from 'react'

export class RaceList extends React.Component {
	state = {
		RaceArr: [],
	}

	componentDidMount() {
		const classUrl = 'http://www.dnd5eapi.co/api/races/'

		fetch(classUrl)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(races => {
				return {
					value: races.name,
					name: races.name
				}
			})
			this.setState({
				raceArr: [{
					value: '',
					display: 'Choose Your Race!',
				}]
				.concat(temp)
			})
		})
	}


	render(){
		return(
			<select>
				{
					this.state.RaceArr.map((race) => 
						<option 
						key={race.value} 
						value={race.value}>
						{race.display}
						</option>)
				}
			</select>
			)
	}

}
