import React from 'react'

export default class Api extends React.Component {
	state = {
		classArr: []
	}



	componentDidMount() {
		const classUrl = 'http://www.dnd5eapi.co/api/classes/'

		fetch(classUrl)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(classes => {
				return(
					<option key={classes.url} >{classes.name}</option>	
					)
			})
			this.setState({
				classArr: temp
			})
			console.log('state', this.state.classArr)
		})
	}

	render(){
		return(
			<Api />
			)
	}

}
