import React from 'react'

export default class CharSheet extends React.Component {
	state = {
		name: ''
	}

	nameChange = (value) => (setState({
		name: this.target.value
	}))

	render(){
		return(
			<div>
				<input 
				type='text'
				value={this.name}
				placeholder='rolfor'
				onChange={nameChange}
				/>
			</div>
			)
	}

}
