import React from 'react'
import RaceList from '../api/raceapi.js'
import ClassList from '../api/classapi.js'

export default class CharSheet extends React.Component {
	state = {
		name: '',
		selectedClass: '',
		selectedRace: ''
	}

	nameChange = (event) => {
		this.setState({
		name: event.target.value
	})}

	importData = (data) => {
		this.setState({
			selectedClass: data,
		})
	}


	render(){
		return(
			<div className='container'>
			<form>
			<div className='row'>
				<div className='col-left'>
					<label htmlFor='name'>Name:</label>
				</div>
				<div className='col-right'>
					<input 
					type='text'
					id='name'
					value={this.state.name}
					placeholder='rolfor'
					onChange={this.nameChange}
					/>
				</div>
			</div>
			<div className='row'>
				<div className='col-left'>
					<label htmlFor='race'>Race:</label>
				</div>
				<div className='col-right'>
					<RaceList id='race'/>
				</div>
			</div>
			<div className='row'>
				<div className='col-left'>
					<label htmlFor='class'>Class:</label>
				</div>
				<div className='col-right'>
					<ClassList id='class' dataImport={this.importData}/>
				</div>
			</div>
			</form>
			{console.log('Class',this.state.selectedClass)}
			{console.log('Race',this.state.selectedRace)}
			{console.log('Name',this.state.name)}
			</div>
			)
	}

}
