import React from 'react'

export class RaceList extends React.Component {
	state = {
		RaceArr: [],
	}

	componentDidMount() {
		const classUrl = 'http://www.dnd5eapi.co/api/races/'

		fetch(classUrl)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(races => {
				return {
					value: races.name,
					name: races.name
				}
			})
			this.setState({
				raceArr: [{
					value: '',
					display: 'Choose Your Race!',

				}]
			})
		})
	}


	render(){
		return(
			<select>
			{this.state.raceArr}
			</select>
			)
	}

}
