import React from 'react'

export class RaceList extends React.Component {
	state = {
		RaceArr: [],
		race: ''
	}

	componentDidMount() {
		const classUrl = 'http://www.dnd5eapi.co/api/races/'

		fetch(classUrl)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(races => {
				return(
					<option key={races.url}>{races.name}</option>	
					)
			})
			this.setState({
				raceArr: temp
			})
		})
	}


	render(){
		return(
			<select onChange={(event) => this.setState({
			race: event.target.value
		})}>
			{this.state.raceArr}
			</select>
			)
	}

}
