import React from 'react'


export default class CharSheet extends React.Component {
	state = {
		name: ''
	}

	componentDidMount() {
	let classArr =[]
	fetch(`http://dnd5eapi.co/api/classes/`)
	.then(resp => resp.json())
	.then(data => classArr.map(data.results))
	console.log(classArr)
}

	nameChange = (event) => {
		this.setState({
		name: event.target.value
	})}

	render(){
		return(
			<div>
			<form>
				<input 
				type='text'
				value={this.state.name}
				placeholder='rolfor'
				onChange={this.nameChange}
				/>
			<select>

			</select>
			</form>
			</div>
			)
	}

}
