import React from 'react'

export default class Api extends React.Component {
	state = {
		selectArr: [],
		selected: '',
		validationError: ''
	}

	componentDidMount() {
		const url = `http://www.dnd5eapi.co/api/${this.props.endpoint}/`

		fetch(url)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(selections => {
				return {
					name: selections.name,
					value: selections.name,
				}
			})
			this.setState({
				selectedArr: [{
					value: '',
					name: this.props.instructions,
				}].concat(temp)
			})
		}).catch(error => console.error('error'))
	}


	render(){
		return(
			<div>
				<select 
					value={this.state.selected}
					onChange={(event) => this.setState({
						selected: event.target.value,
						validationError: event.target.value === '' 
						? 'You must select something'
						: ''
					})}>
					{
						this.state.selectArr.map((choice) => 
							<option 
								key={choice.value} 
								value={choice.value}>
								{choice.name}
							</option>)
					}
				</select>
				<div style={{color: 'red', marginTop: '5px'}}>
					{this.state.validationError}
				</div>
			</div>

		)
	}
}
