import React from 'react'

export default class ClassList extends React.Component {
	state = {
		classArr: [],
		validationError: ''
	}

	componentDidMount() {
		const classUrl = 'http://www.dnd5eapi.co/api/classes/'

		fetch(classUrl)
		.then(resp => resp.json())
		.then(data => {
			let temp = data.results.map(classes => {
				return {
					name: classes.name,
					value: classes.name,
				}
			})
			this.setState({
				classArr: [{
					value: '',
					name: 'Choose Your Class!',
				}].concat(temp)
			})
		}).catch(error => console.error('error'))
	}


	render(){
		return(
			<div>
				<select 
					value={this.state.selectedClass}
					onChange={(event) => 
						(this.setState({
						validationError: event.target.value === '' 
						? 'You must select a class'
						: ''
					}),
					this.props.setState({
						selectedClass: event.target.value,
					})
					)}>
					{
						this.state.classArr.map((dndClass) => 
							<option 
								key={dndClass.value} 
								value={dndClass.value}>
								{dndClass.name}
							</option>)
					}
				</select>
				<div style={{color: 'red', marginTop: '5px'}}>
					{this.state.validationError}
				</div>
			</div>

		)
	}
}
